package com.javalec.base;

public class Student {
	//field
	String name;
	int score;
	
	//constructor
	public Student(String name, int score) {
		this.name = name;
		this.score = score;
		// TODO Auto-generated constructor stub
	}


	//method
	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public int getScore() {
		return score;
	}

	public void setScore(int score) {
		this.score = score;
	}
	

	
	
}
